sys_script_include.script:
var ServerCLI = Class.create();
ServerCLI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	callCodeEntry : function() {
		var returnValue = "";

		var serverCodeName = this.getParameter('sysparm_cli_server_code_entry');
		var profileSysId = this.getParameter('sysparm_cli_profile_sys_id');

		var codeGr = new GlideRecord("x_293397_web_cli_code_entries");
		codeGr.addQuery('active',true);
		codeGr.addQuery('name',serverCodeName);
		codeGr.query();
		if (codeGr.next()) {
			if (this.isCodeInProfile(profileSysId,codeGr.getUniqueValue())) {
				returnValue = true;
				eval(codeGr.getValue('script'));
			}
			else {
				returnValue = "Server commmand not available in profile (" + profileSysId + "," + serverCodeName + ")";
			}
		}
		else {
			returnValue = "Server commmand not found (" + serverCodeName + ")";
		}
		return returnValue;
	},
	isCodeInProfile : function(profileUniqueId, codeUniqueId) {
		var returnValue = false;
		var profileGr = new GlideRecord("x_293397_web_cli_m2m_profiles_code_entries");
		profileGr.addQuery("profiles",profileUniqueId);
		profileGr.addQuery("code_entries",codeUniqueId);
		profileGr.query();
		if (profileGr.next()) {
			returnValue = true;
		}
		return returnValue;
	},
	type: 'ServerCLI'
});
