sys_script_include.script:
var ConfigureCLI = Class.create();
ConfigureCLI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	loadProfile : function() {		
		var profileUniqueId = this.getParameter('sysparm_cli_profile_sys_id');

		//************
		// Command List
		var commandList = [];

		var profileCommandGr = new GlideRecord("x_293397_web_cli_m2m_profiles_commands");
		profileCommandGr.addQuery("profiles",profileUniqueId);
		profileCommandGr.query();
		while (profileCommandGr.next()) {
			var cmdGr = new GlideRecord("x_293397_web_cli_commands");
			cmdGr.addQuery("active", true);
			cmdGr.addQuery("sys_id",profileCommandGr.getValue('commands'));
			cmdGr.query();
			if (cmdGr.next()) {
				commandList.push({
					name:cmdGr.getValue('name'),
					script:cmdGr.getValue('script'),
					description:cmdGr.getValue('description'),
					usage:cmdGr.getValue('usage')
				});
			}
		}
		gs.info('COMMANDS:  ' + JSON.stringify(commandList));
		//************

		//************
		// Task List
		var taskList = [];

		var profileTaskGr = new GlideRecord("x_293397_web_cli_m2m_profiles_scheduled_tasks");
		profileTaskGr.addQuery("profiles",profileUniqueId);
		profileTaskGr.query();
		while (profileTaskGr.next()) {
			var taskGr = new GlideRecord("x_293397_web_cli_scheduled_tasks");
			taskGr.addQuery("active", true);
			taskGr.addQuery("sys_id",profileTaskGr.getValue('scheduled_tasks'));
			taskGr.query();
			if (taskGr.next()) {
				taskList.push({
					script:taskGr.getValue('script'),
					description:taskGr.getValue('description'),
					interval:taskGr.getValue('interval')
				});
			}
		}
		gs.info('TASKS:  ' + JSON.stringify(taskList));
		//************

		var returnValue = {
			success : (commandList.length > 0 || taskList.length > 0) ? true : false,
			commands : commandList,
			tasks : taskList
		};

		return JSON.stringify(returnValue);
	},
	type: 'ConfigureCLI'
});
